and t.contact like concat('%', #{contact}, '%')
and t.contact_type in
#{item}
and t.record_time >= #{recordBeginTime}
and t.record_time <= #{recordEndTime}
and t.row_memory_type in
#{item}
and t.memory_source in
#{item}
and t.memory_associative_status in
#{item}
